// ProductDetailScreen.js

import React, { useRef } from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import Swiper from 'react-native-swiper/';
import Icon from 'react-native-vector-icons/FontAwesome';

const ProductDetailScreen = ({ route, navigation }) => {
  const { product } = route.params;
  const swiperRef = useRef(null);

  const decodeBuffer = (buffer) => {
    return new TextDecoder('utf-8').decode(new Uint8Array(buffer));
  };

  const goNext = () => {
    swiperRef.current.scrollBy(1, true);
  };

  const goPrev = () => {
    swiperRef.current.scrollBy(-1, true);
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-left" size={24} color="black" />
        </TouchableOpacity>
      </View>
      <Swiper
        ref={swiperRef}
        style={styles.swiperContainer}
        loop={false}
        showsPagination={false}
      >
        {product.iProductImg && (
          <Image
            source={{ uri: `http://127.0.0.1:8000/media/${decodeBuffer(product.iProductImg.data)}` }}
            style={styles.image}
          />
        )}
        {product.iInfoImg && (
          <Image
            source={{ uri: `http://127.0.0.1:8000/media/${decodeBuffer(product.iInfoImg.data)}` }}
            style={styles.image}
          />
        )}
        {/* Agrega más imágenes según tus necesidades */}
      </Swiper>
      <View style={styles.detailsContainer}>
        <Text style={styles.productName}>{product.nombre_producto}</Text>
        <Text style={styles.detailsText}>Categoría: {product.id_categoria}</Text>
        <Text style={styles.detailsText}>Marca: {product.id_marca}</Text>
        <Text style={styles.detailsText}>Sabor: {product.sabor}</Text>
        <Text style={styles.detailsText}>Presentación: {product.presentacion} lb(s)</Text>
        <Text style={styles.detailsText}>Precio: {product.precio_pub}</Text>
        <Text style={styles.detailsText}>Descripción: {product.descripcion}</Text>
        {/* Agrega más detalles según tus necesidades */}
      </View>
      <View style={styles.navigationContainer}>
        <TouchableOpacity onPress={goPrev} style={styles.navigationButton}>
          <Icon name="chevron-left" size={30} color="black" />
        </TouchableOpacity>
        <TouchableOpacity onPress={goNext} style={styles.navigationButton}>
          <Icon name="chevron-right" size={30} color="black" />
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'center',
    padding: 10,
  },
  backButton: {
    marginRight: 10,
  },
  swiperContainer: {
    height: 200, // Ajusta la altura según tus necesidades
  },
  image: {
    flex: 1,
    width: '100%',
    height: '100%',
    borderRadius: 8,
    resizeMode: 'cover',
  },
  detailsContainer: {
    flex: 1,
    padding: 20,
  },
  productName: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  detailsText: {
    fontSize: 18,
    marginBottom: 8,
  },
  navigationContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
  },
  navigationButton: {
    padding: 10,
  },
});

export default ProductDetailScreen;
