// Sidebar.js

import React, { useState, useRef, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Animated, Dimensions } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const Sidebar = ({ isOpen, onClose }) => {
  const windowWidth = Dimensions.get('window').width;
  const sidebarWidth = windowWidth * 0.75; // Definir el ancho del sidebar
  const sidebarTranslateX = useRef(new Animated.Value(-sidebarWidth)).current;

  useEffect(() => {
    Animated.timing(sidebarTranslateX, {
      toValue: isOpen ? 0 : -sidebarWidth,
      duration: 300,
      useNativeDriver: false,
    }).start();
  }, [isOpen]);

  return (
    <Animated.View style={[styles.sidebar, { width: sidebarWidth, transform: [{ translateX: sidebarTranslateX }] }]}>
      <TouchableOpacity style={styles.closeButton} onPress={onClose}>
        <Icon name="times" size={30} color="white" />
      </TouchableOpacity>
      <Text style={styles.menuItem}>Opción 1</Text>
      <Text style={styles.menuItem}>Opción 2</Text>
      <Text style={styles.menuItem}>Opción 3</Text>
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    backgroundColor: 'black',
    padding: 16,
    position: 'absolute',
    top: 0,
    left: 0,
    height: '100%',
    zIndex: 2,
  },
  closeButton: {
    alignSelf: 'flex-end',
    marginBottom: 20,
  },
  menuItem: {
    color: 'white',
    fontSize: 18,
    marginBottom: 10,
  },
});

export default Sidebar;
