import React, { useState, useRef, useEffect } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, Animated, Dimensions } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const Navbar = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const windowWidth = Dimensions.get('window').width;

  const menuWidth = useRef(new Animated.Value(0)).current;

  const toggleMenu = () => {
    setMenuOpen((prev) => !prev);
  };

  useEffect(() => {
    Animated.timing(menuWidth, {
      toValue: isMenuOpen ? windowWidth / 2 : 0,
      duration: 300,
      useNativeDriver: false,
    }).start();
  }, [isMenuOpen]);

  return (
    <View style={[styles.navbar, styles.navbarWithMargin]}>
      <TouchableOpacity style={styles.menuIcon} onPress={toggleMenu}>
        <Icon name="bars" size={30} color="white" />
      </TouchableOpacity>
      <View style={styles.titleContainer}>
        <Image
          source={require('../assets/img/GIcon.png')}
          style={styles.logo}
          resizeMode="contain"
        />
        <Text style={styles.navbarText}>Tuiranfit</Text>
      </View>
      <Animated.View style={[styles.sideMenu, { width: menuWidth }]}>
        {/* Agrega aquí los elementos del menú lateral */}
        <TouchableOpacity onPress={toggleMenu}>
          <Text style={styles.menuItem}>Opción 1</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={toggleMenu}>
          <Text style={styles.menuItem}>Opción 2</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={toggleMenu}>
          <Text style={styles.menuItem}>Opción 3</Text>
        </TouchableOpacity>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  navbar: {
    backgroundColor: 'black',
    flexDirection: 'row',
    padding: 10,
    alignItems: 'center',
    justifyContent: 'space-between',
    borderRadius: 10,
  },
  navbarWithMargin: {
    marginBottom: 10,
  },
  menuIcon: {
    marginLeft: 10,
  },
  titleContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    width: 30,
    height: 30,
    marginRight: 10,
  },
  navbarText: {
    color: 'white',
    fontSize: 20,
    fontWeight: 'bold',
  },
  sideMenu: {
    position: 'absolute',
    top: 0,
    right: 0,
    height: '100%',
    backgroundColor: 'black',
    padding: 10,
    borderRadius: 10,
    zIndex: 1,
    justifyContent: 'center',
  },
  menuItem: {
    color: 'white',
    fontSize: 16,
    marginVertical: 5,
  },
});

export default Navbar;
