import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image } from 'react-native';

const HomeScreen = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Hacer la solicitud al servidor
    fetch('http://localhost:5000/productos')
      .then((response) => {
        console.log('Respuesta del servidor:', response);
        return response.json();
      })
      .then((data) => {
        console.log('Datos de productos:', data);
        setProductos(data);
      })
      .catch((error) => {
        console.error('Error al obtener productos:', error);
      });
  }, []);

  return (
    <View style={styles.container}>
      
      <FlatList
        data={productos}
        keyExtractor={(item) => item.id_producto.toString()}
        renderItem={({ item }) => (
          <TouchableOpacity style={styles.card}>
            <Image source={{ uri: `data:image/png;base64,${item.iProductImg.data.toString('base64')}` }} style={styles.image} />
            <Text style={styles.productName}>{item.nombre_producto}</Text>
            <Text style={styles.description}>{item.descripcion}</Text>
            <Text style={styles.price}>Precio: ${item.precio}</Text>
            <Text style={styles.expiryDate}>Fecha de vencimiento: {new Date(item.fechaven).toLocaleDateString()}</Text>
            <Text style={styles.text}>Cantidad: {item.cantidad}</Text>

          </TouchableOpacity>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 16,
    marginVertical: 8,
  },
  image: {
    width: 100,
    height: 100,
    marginBottom: 8,
  },
  productName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 4,
  },
  description: {
    marginBottom: 8,
  },
  price: {
    fontWeight: 'bold',
    marginBottom: 4,
  },
  expiryDate: {
    color: 'red',
  },
  
});

export default HomeScreen;
