// navbarrr.js

import React, { useState } from 'react';
import { View, Image, Text, StyleSheet, TouchableOpacity, Modal, Pressable } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const Navbar = ({ onLogoutPress }) => {
  const [modalVisible, setModalVisible] = useState(false);

  const handleIconPress = () => {
    setModalVisible(!modalVisible);
  };

  const handleLogout = () => {
    setModalVisible(false);
    onLogoutPress();
  };

  return (
    <View style={[styles.navbar, styles.navbarWithMargin]}>
      <View style={styles.leftContainer}>
        <Image
          source={require('../assets/img/GIcon.png')}
          style={styles.logo}
          resizeMode="contain"
        />
        <Text style={styles.navbarText}>Tuiranfit</Text>
      </View>
      <TouchableOpacity onPress={handleIconPress}>
        <Icon name="ellipsis-v" size={20} color="white" style={styles.icon} />
      </TouchableOpacity>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Pressable style={styles.closeButton} onPress={() => setModalVisible(!modalVisible)}>
              <Text style={styles.textStyle}>X</Text>
            </Pressable>
            <Text style={styles.modalText}>Cerrar Sesión</Text>
            <TouchableOpacity onPress={handleLogout}>
              <Text style={styles.logoutText}>Aceptar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  navbar: {
    backgroundColor: 'black',
    padding: 10,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  navbarWithMargin: {
    marginBottom: 20,
  },
  leftContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  icon: {
    marginLeft: 10, // Ajusta la posición del ícono según sea necesario
  },
  logo: {
    width: 30,
    height: 30,
    marginRight: 10,
  },
  navbarText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  closeButton: {
    position: 'absolute',
    top: 10,
    right: 10,
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  modalText: {
    marginBottom: 15,
    textAlign: 'center',
  },
  logoutText: {
    color: 'red',
    fontWeight: 'bold',
  },
});

export default Navbar;
