import React, { useEffect, useState } from 'react';
import { View, Text, Image, FlatList, TouchableOpacity } from 'react-native';

const HomeScreen = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Hacer la solicitud al servidor
    fetch('http://tu-servidor:5000/productos')
      .then((response) => response.json())
      .then((data) => {
        setProductos(data);
      })
      .catch((error) => {
        console.error('Error al obtener productos:', error);
      });
  }, []);

  const styles = {
    container: {
      flex: 1,
      padding: 16,
    },
    card: {
      backgroundColor: '#fff',
      borderRadius: 8,
      padding: 16,
      marginBottom: 16,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.25,
      shadowRadius: 4,
      elevation: 5,
    },
    image: {
      width: '100%',
      height: 200,
      borderRadius: 8,
      marginBottom: 12,
    },
    text: {
      fontSize: 16,
      marginBottom: 8,
    },
  };

  return (
    <View style={styles.container}>
      <Text>Productos:</Text>
      {productos.length === 0 ? (
        <Text>No se encontraron productos.</Text>
      ) : (
        <FlatList
          data={productos}
          keyExtractor={(item) => item.id_producto.toString()}
          renderItem={({ item }) => (
            <TouchableOpacity style={styles.card}>
              <Image source={{ uri: `http://localhost:5000/${item.iProductImg}` }} style={styles.image} />
              <Text style={styles.text}>{item.nombre_producto}</Text>
              <Text style={styles.text}>{item.descripcion}</Text>
              <Text style={styles.text}>Precio: ${item.precio}</Text>
              <Text style={styles.text}>Fecha de vencimiento: {new Date(item.fechaven).toLocaleDateString()}</Text>
              <Text style={styles.text}>Cantidad: {item.cantidad}</Text>
            </TouchableOpacity>
          )}
        />
      )}
    </View>
  );
};

export default HomeScreen;
