// HomeScreen.js

import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image } from 'react-native';
import Navbar from './navbarrr';
import Sidebar from './sidebarrr';

const HomeScreen = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Hacer la solicitud al servidor
    fetch('http://localhost:5000/productos')
      .then((response) => response.json())
      .then((data) => {
        console.log('Datos de productos:', data);
        setProductos(data);
      })
      .catch((error) => {
        console.error('Error al obtener productos:', error);
      });
  }, []);

  const arrayBufferToBase64 = (buffer) => {
    let binary = '';
    const bytes = new Uint8Array(buffer);
    const len = bytes.byteLength;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return btoa(binary);
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Navbar />
      </View>
      <View style={styles.contentContainer}>
        <Sidebar />
        <FlatList
          data={productos}
          keyExtractor={(item) => item.id_producto.toString()}
          renderItem={({ item }) => (
            <TouchableOpacity style={styles.card}>
              {item.iProductImg ? (
                <Image
                  source={{ uri: `data:image/png;base64,${arrayBufferToBase64(item.iProductImg)}` }}
                  style={styles.image}
                />
              ) : (
                <Text>Imagen no disponible</Text>
              )}
              <View style={styles.cardContent}>
                <Text style={styles.productName}>{item.nombre_producto}</Text>
                <Text style={styles.description}>{item.descripcion}</Text>
                <Text style={styles.price}>Precio: ${item.precio}</Text>
                <Text style={styles.expiryDate}>
                  Vencimiento: {new Date(item.fechaven).toLocaleDateString()}
                </Text>
                <Text style={styles.stock}>Cantidad: {item.cantidad}</Text>
              </View>
            </TouchableOpacity>
          )}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  header: {
    height: 60, // Ajusta seg√∫n sea necesario
    backgroundColor: '', // Color del Navbar
    justifyContent: 'center',
    alignItems: 'center',
  },
  contentContainer: {
    flex: 1,
    flexDirection: 'row',
  },
  card: {
    backgroundColor: '#f9f9f9',
    borderRadius: 12,
    padding: 16,
    marginBottom: 16,
    marginLeft: 16,
    marginRight: 16,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.2,
    shadowRadius: 4,
    elevation: 5,
    width: '40%',
  },
  image: {
    width: '100%',
    height: 120,
    borderRadius: 8,
    marginBottom: 8,
  },
  cardContent: {
    flex: 1,
    flexDirection: 'column',
  },
  productName: {
    fontSize: 16,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 14,
    marginBottom: 8,
  },
  price: {
    fontSize: 14,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  expiryDate: {
    fontSize: 14,
    marginBottom: 8,
    color: 'red',
  },
  stock: {
    fontSize: 14,
    fontWeight: 'bold',
  },
});

export default HomeScreen;
