// HomeScreen.js
import React from 'react';
import { View, Text, Button } from 'react-native';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { useNavigation } from '@react-navigation/native';

const Drawer = createDrawerNavigator();
const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

const TabScreen = () => (
  <Tab.Navigator>
    <Tab.Screen name="Página 1" component={Pagina1} />
    <Tab.Screen name="Página 2" component={Pagina2} />
  </Tab.Navigator>
);

const Pagina1 = () => (
  <View>
    <Text>Página 1</Text>
  </View>
);

const Pagina2 = () => (
  <View>
    <Text>Página 2</Text>
  </View>
);

const CerrarSesion = () => {
  const navigation = useNavigation();

  const handleCerrarSesion = () => {
    // Lógica para cerrar sesión, por ejemplo, eliminar el token
    // y redirigir a la pantalla de inicio de sesión
    console.log('Cerrando sesión...');
    navigation.navigate('Login');
  };

  return (
    <View>
      <Text>Cerrar Sesión</Text>
      <Button title="Cerrar Sesión" onPress={handleCerrarSesion} />
    </View>
  );
};

const HomeScreen = () => {
  return (
    <Drawer.Navigator initialRouteName="Tabs">
      <Drawer.Screen name="Tabs" component={TabScreen} />
      <Drawer.Screen name="Cerrar Sesión" component={CerrarSesion} />
    </Drawer.Navigator>
  );
};

export default HomeScreen;
