// HomeScreen.js

import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image, Alert } from 'react-native';
import Navbar from './navbarrr';
import Sidebar from './sidebarrr';
import Icon from 'react-native-vector-icons/FontAwesome';

const HomeScreen = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Hacer la solicitud al servidor
    fetch('http://localhost:5000/productos')
      .then((response) => response.json())
      .then((data) => {
        console.log('Datos de productos:', data);
        setProductos(data);
      })
      .catch((error) => {
        console.error('Error al obtener productos:', error);
      });
  }, []);

  const arrayBufferToBase64 = (buffer) => {
    let binary = '';
    const bytes = new Uint8Array(buffer);
    const len = bytes.byteLength;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return btoa(binary);
  };

  const handleLogout = () => {
    Alert.alert(
      'Cerrar Sesión',
      '¿Estás seguro de que deseas cerrar sesión?',
      [
        { text: 'Cancelar', style: 'cancel' },
        { text: 'Aceptar', onPress: logout },
      ],
      { cancelable: false }
    );
  };

  const logout = () => {
    navigation.replace('Login'); // Reemplaza 'Login' con el nombre de tu pantalla de inicio de sesión

  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Navbar onLogoutPress={handleLogout} />
      </View>
      <View style={styles.contentContainer}>
        <View style={styles.sidebar}>
          <Sidebar />
        </View>
        <View style={styles.cardsContainer}>
          <FlatList
            data={productos}
            keyExtractor={(item) => item.id_producto.toString()}
            renderItem={({ item }) => (
              <TouchableOpacity style={styles.card}>
                <View style={styles.cardContent}>
                  {item.iProductImg ? (
                    <Image
                      source={{ uri: `data:image/png;base64,${arrayBufferToBase64(item.iProductImg)}` }}
                      style={styles.image}
                    />
                  ) : (
                    <Text>Imagen no disponible</Text>
                  )}
                  <Text style={styles.productName}>{item.nombre_producto}</Text>
                  <Text style={styles.description}>{item.descripcion}</Text>
                  <Text style={styles.price}>Precio: ${item.precio}</Text>
                  <Text style={styles.stock}>Cantidad: {item.cantidad}</Text>
                  <Text style={styles.expiryDate}>
                    Vencimiento: {new Date(item.fechaven).toLocaleDateString()}
                  </Text>
                </View>
              </TouchableOpacity>
            )}
          />
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  header: {
    height: 60,
    backgroundColor: 'black',
    justifyContent: 'center',
    alignItems: 'center',
  },
  contentContainer: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    width: 150,
    backgroundColor: '#333',
  },
  cardsContainer: {
    flex: 1,
    marginLeft: 16,
    marginRight: 16,
    marginBottom: 16,
    marginTop: 16,
  },
  card: {
    backgroundColor: '#f9f9f9',
    borderRadius: 12,
    borderWidth: 1,
    borderColor: 'red',
    padding: 16,
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.2,
    shadowRadius: 4,
    elevation: 5,
    width: '100%',
  },
  image: {
    width: '100%',
    height: 120,
    borderRadius: 8,
    marginBottom: 8,
  },
  cardContent: {
    flex: 1,
    flexDirection: 'column',
  },
  productName: {
    fontSize: 16,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 14,
    marginBottom: 8,
  },
  price: {
    fontSize: 14,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  expiryDate: {
    fontSize: 14,
    marginBottom: 8,
    color: 'red',
  },
  stock: {
    fontSize: 14,
    fontWeight: 'bold',
  },
});

export default HomeScreen;
