import React from 'react';
import { StyleSheet, Text, View, Image, Pressable, ScrollView } from 'react-native';
import { TextInput, Button } from 'react-native-paper';
import { Formik } from 'formik';

export default function App() {
  return (
    <ScrollView contentContainerStyle={styles.allContainer}>
      <Image
        source={require('./assets/img/bwh.webp')}
        style={styles.backgroundImage}
      />
      <View style={styles.formContainer}>
        <Image
          source={require('./assets/img/GIcon.png')}
          style={styles.image}
          resizeMode="contain"
        />
        <Formik
          initialValues={{ nombre: '', correo: '' }}
          onSubmit={(values) => {
            console.log(values);
          }}
        >
          {({ handleChange, handleBlur, handleSubmit, values }) => (
            <View style={{ width: '100%' }}>
              <TextInput
                label="Nombre"
                mode="outlined"
                onChangeText={handleChange('nombre')}
                onBlur={handleBlur('nombre')}
                value={values.nombre}
                style={styles.input}
              />
              <TextInput
                label="Correo"
                mode="outlined"
                onChangeText={handleChange('correo')}
                onBlur={handleBlur('correo')}
                value={values.correo}
                style={styles.input}
                secureTextEntry
              />
              <Button mode="contained" onPress={handleSubmit} style={{backgroundColor:'red', width:''}}>
                <Text style={{color:'white', fontWeight:'bold'}}>Entrar</Text>
              </Button>
            </View>
          )}
        </Formik>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  allContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  backgroundImage: {
    flex: 1,
    position: 'absolute',
    width: '100%',
    height: '100%',
    resizeMode: 'cover',
  },
  formContainer: {
    alignItems: 'center',
    width: '80%',
    padding: 30,
    borderRadius: 5,
    backgroundColor: 'rgba(255, 255, 255, 0.7)', // Fondo semi-transparente blanco
  },
  image: {
    aspectRatio: 1,
    height: 100,
    marginBottom: 20,
  },
  input: {
    width: '100%',
    marginBottom: 15,
  },
  button: {
    marginTop: 20,
  },
});
