import React, { useState } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const Navbar = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    console.log('Toggle menu'); // Agregamos un console.log para verificar si la función se llama
    setMenuOpen(prevState => !prevState); // Actualizamos el estado de manera segura
  };

  return (
    <View style={[styles.navbar, styles.navbarWithMargin]}>
      <TouchableOpacity style={styles.menuIcon} onPress={toggleMenu}>
        <Icon name="bars" size={30} color="white" />
      </TouchableOpacity>
      <View style={styles.titleContainer}>
        <Image
          source={require('../assets/img/GIcon.png')}
          style={styles.logo}
          resizeMode="contain"
        />
        <Text style={styles.navbarText}>Tuiranfit</Text>
      </View>
      {/* Menú lateral */}
      {isMenuOpen && (
        <View style={styles.sideMenu}>
          {/* Agrega aquí los elementos del menú lateral */}
          <Text style={styles.menuItem}>Opción 1</Text>
          <Text style={styles.menuItem}>Opción 2</Text>
          <Text style={styles.menuItem}>Opción 3</Text>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  navbar: {
    backgroundColor: 'black',
    flexDirection: 'row',
    padding: 10,
    alignItems: 'center',
    justifyContent: 'space-between',
    borderRadius: 10,
  },
  navbarWithMargin: {
    marginBottom: 10,
  },
  menuIcon: {
    marginLeft: 10,
  },
  titleContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logo: {
    width: 30,
    height: 30,
    marginRight: 10,
  },
  navbarText: {
    color: 'white',
    fontSize: 20,
    fontWeight: 'bold',
  },
  sideMenu: {
    position: 'absolute',
    top: 0,
    left: 0,
    backgroundColor: 'black',
    padding: 10,
    borderRadius: 10,
    zIndex: 1,
  },
  menuItem: {
    color: 'white',
    fontSize: 16,
    marginVertical: 5,
  },
});

export default Navbar;
