import React from 'react';
import { StyleSheet, Text, View, TextInput, Image, Pressable, Dimensions, ScrollView } from 'react-native';
import { Formik } from 'formik';

export default function App() {
  const { width: screenWidth, height: screenHeight } = Dimensions.get('window');
  const maxImageSize = Math.min(screenWidth, screenHeight) * 0.4;

  return (
    <ScrollView contentContainerStyle={styles.allContainer}>
      <Image
        source={require('./assets/img/bwh.webp')}
        style={styles.backgroundImage}
      />
      <View style={styles.formContainer}>
        {/* <Text style={styles.header}>Log In</Text> */}
        <View style={styles.logoContainer}>
          <Image
            source={require('./assets/img/GIcon.png')}
            style={[styles.image, { width: maxImageSize, height: maxImageSize, borderWidth: 4, borderColor: 'red', borderRadius: 75 }]}
            resizeMode="contain"
          />
        </View>
        <Formik
          initialValues={{ nombre: '', correo: '' }}
          onSubmit={(values) => {
            // Aquí puedes manejar los valores del formulario cuando se envía.
            console.log(values);
          }}
        >
          {({ handleChange, handleBlur, handleSubmit, values }) => (
            <View style={{ width: '100%' }}>
              <TextInput
                placeholder="Nombre"
                onChangeText={handleChange('nombre')}
                onBlur={handleBlur('nombre')}
                value={values.nombre}
                style={styles.input}
              />
              <TextInput
                placeholder="Correo"
                onChangeText={handleChange('correo')}
                onBlur={handleBlur('correo')}
                value={values.correo}
                style={styles.input}
              />
              <Pressable title="Enviar" onPress={handleSubmit} style={styles.button}>
                <Text style={styles.buttonText}>Entrar</Text>
              </Pressable>
            </View>
          )}
        </Formik>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  allContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  backgroundImage: {
    flex: 1,
    position: 'absolute',
    width: '100%',
    height: '100%',
    resizeMode: 'cover',
  },
  header: {
    fontSize: 40,
    fontWeight: 'bold',
    marginBottom: 0,
    color: 'black',
    width: '100%',
    textAlign: 'center',
    screenWidth:30
  },
  input: {
    width: '100%',
    borderColor: 'white',
    borderWidth: 3,
    padding: 10,
    marginBottom: 10,
    borderRadius: 8,
    color: 'white',
    underlineColorAndroid: 'red',
  },
  formContainer: {
    alignItems: 'center',
    width: '80%',
    padding: 30,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5
  },
  image: {
    aspectRatio: 1,
    heihgt: '100%',
    marginBottom: 0,
  },
  logoContainer: {
    width: '100%',
    aspectRatio: 1,
    marginBottom: 0,
    marginTop: 0,
    justifyContent: 'center',
    alignItems: 'center',
  },
  button: {
    backgroundColor: 'red',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
    marginTop: 10,
    width: '70%',
    alignSelf: 'center',
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
  },
});
