import React from 'react';
import { View, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { StyleSheet, Image, Pressable, ScrollView } from 'react-native';
import { TextInput, Button, Provider as PaperProvider, DefaultTheme } from 'react-native-paper';
import { Formik } from 'formik';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';

const LoginScreen = () => {
//   const navigation = useNavigation();

//   const handleLogin = () => {
//     // Lógica de inicio de sesión aquí
//     // Después de iniciar sesión, navegar a la vista de inicio
//     navigation.navigate('Home');
//   };

  const theme = {
    ...DefaultTheme,
    colors: {
      ...DefaultTheme.colors,
      primary: 'red',
      background: 'transparent', 
      // Puedes agregar más propiedades según tus necesidades
    },
  };

  const handleLogin = async (values) => {
    try {
      const response = await axios.post('http://localhost:5000/login', values);
  
      const token = response.data.token;
  
      // Guardar el token en AsyncStorage para su uso posterior
      await AsyncStorage.setItem('token', token);
  
      // Imprimir el token en la consola para verificar
      console.log('Token guardado en AsyncStorage:', token);
  
      // Navegar a la pantalla de inicio
      navigation.navigate('Home');
  
      // Mensaje por consola en caso de inicio de sesión exitoso
      console.log('Inicio de sesión exitoso');
    } catch (error) {
      console.error('Error al iniciar sesión:', error.message);
    }
  };
  

  return (
    <PaperProvider theme={theme}>
    <ScrollView contentContainerStyle={styles.allContainer}>
      <Image
        source={require('../assets/img/bwh.webp')}
        style={styles.backgroundImage}
      />
      {/* <Text style={{color:'black', fontWeight:'bold', fontSize:45, fontFamily:''}}>Iniciar Sesión</Text> */}
      <View style={styles.formContainer}>
        <Image
          source={require('../assets/img/GIcon.png')}
          style={styles.image}
          resizeMode="contain"
        />

        <Formik
          initialValues={{ correo: '', contra: '' }}
          onSubmit={handleLogin}
        >
          {({ handleChange, handleBlur, handleSubmit, values }) => (
            <View style={{ width: '100%' }}>
              <TextInput
                label="Correo Electrónico"
                mode="outlined"
                theme={{ colors: { text: 'black', primary: 'red' } }}
                onChangeText={handleChange('correo')}
                onBlur={handleBlur('correo')}
                value={values.correo}
                style={styles.input}
                underlineColor="black"
              />
              <TextInput
                label="Contraseña"
                mode="outlined"
                onChangeText={handleChange('contra')} 
                onBlur={handleBlur('contra')}
                theme={{ colors: { text: 'white', primary: 'red' } }}
                value={values.contra}
                style={styles.input}
                
                secureTextEntry
              />
              
              <Button mode="contained" onPress={handleSubmit} style={{backgroundColor:'red', width:'50%', alignSelf:'center', marginTop:8}}>
                <Text style={{color:'white', fontWeight:'bold'}}>Entrar</Text>
              </Button>
            </View>
          )}
        </Formik>
      </View>
    </ScrollView>
    </PaperProvider>
  );
};

const styles = StyleSheet.create({
    allContainer: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    backgroundImage: {
      flex: 1,
      position: 'absolute',
      width: '100%',
      height: '100%',
      resizeMode: 'cover',
    },
    formContainer: {
      alignItems: 'center',
      width: '80%',
      padding: 30,
      borderRadius: 5,
      backgroundColor: 'white',
      borderRadius:10,
      paddingVertical:35,
      borderWidth:3,
    },
    image: {
      aspectRatio: 1,
      height: 100,
      marginBottom: 20,
      borderWidth:6,
      borderColor:'red',
      borderRadius:70
    },
    input: {
      width: '100%',
      marginBottom: 15,
      backgroundColor:'white',
    },
    button: {
      marginTop: 20,
    },
  });

export default LoginScreen;
