import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image } from 'react-native';

const HomeScreen = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Hacer la solicitud al servidor
    fetch('http://localhost:5000/productos')
      .then((response) => response.json())
      .then((data) => {
        setProductos(data);
      })
      .catch((error) => {
        console.error('Error al obtener productos:', error);
      });
  }, []);

  const renderImage = (item) => {
    if (item.iProductImg && item.iProductImg.data) {
      const base64 = Buffer.from(item.iProductImg.data).toString('base64');
      return (
        <Image 
          source={{ uri: `data:image/png;base64,${base64}` }} 
          style={styles.image} 
          onError={(e) => console.log('Error de imagen:', e.nativeEvent.error)}
        />
      );
    } else {
      return <Text>No hay imagen disponible</Text>;
    }
  };

  return (
    <View style={styles.container}>
      <Text>Productos:</Text>
      <FlatList
        data={productos}
        keyExtractor={(item) => item.id_producto.toString()}
        renderItem={({ item }) => (
          <TouchableOpacity style={styles.card}>
            {renderImage(item)}
            <Text style={styles.productName}>{item.nombre_producto}</Text>
            <Text style={styles.description}>{item.descripcion}</Text>
            <Text style={styles.price}>Precio: ${item.precio}</Text>
            <Text style={styles.expiryDate}>Fecha de vencimiento: {new Date(item.fechaven).toLocaleDateString()}</Text>
            <Text style={styles.stock}>Cantidad: {item.cantidad}</Text>
          </TouchableOpacity>
        )}
      />
    </View>
  );

};

function arrayBufferToBase64(buffer) {
  let binary = '';
  const bytes = new Uint8Array(buffer);
  const len = bytes.byteLength;
  for (let i = 0; i < len; i++) {
    binary += String.fromCharCode(bytes[i]);
  }
  return btoa(binary);
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 8,
    padding: 16,
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  image: {
    width: '100%',
    height: 200,
    borderRadius: 8,
    marginBottom: 12,
  },
  productName: {
    fontSize: 16,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 14,
    marginBottom: 8,
  },
  price: {
    fontSize: 14,
    marginBottom: 8,
    fontWeight: 'bold',
  },
  expiryDate: {
    fontSize: 14,
    marginBottom: 8,
    color: 'red',
  },
  stock: {
    fontSize: 14,
    fontWeight: 'bold',
  },
});

export default HomeScreen;
