// ProductDetailScreen.js

import React from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import SwiperFlatList from 'react-native-swiper-flatlist';
import Icon from 'react-native-vector-icons/FontAwesome';

const ProductDetailScreen = ({ route, navigation }) => {
  const { product } = route.params;

  const decodeBuffer = (buffer) => {
    return new TextDecoder('utf-8').decode(new Uint8Array(buffer));
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
          <Icon name="arrow-left" size={24} color="black" />
        </TouchableOpacity>
      </View>
      <View style={styles.contentContainer}>
        <SwiperFlatList
          data={[product.iProductImg, product.iInfoImg]}
          renderItem={({ item }) => (
            <Image
              source={{ uri: `http://127.0.0.1:8000/media/${decodeBuffer(item.data)}` }}
              style={styles.image}
            />
          )}
          paginationActiveColor="#000"
          paginationDefaultColor="#333"
          paginationStyleItem={{ width: 10, height: 10 }}
        />
        <View style={styles.detailsContainer}>
          <Text style={styles.productName}>{product.nombre_producto}</Text>
          <Text style={styles.detailsText}>Categoría: {product.id_categoria}</Text>
          <Text style={styles.detailsText}>Marca: {product.id_marca}</Text>
          <Text style={styles.detailsText}>Sabor: {product.sabor}</Text>
          <Text style={styles.detailsText}>Presentación: {product.presentacion} lb(s)</Text>
          <Text style={styles.detailsText}>Precio: {product.precio_pub}</Text>
          <Text style={styles.description}>{product.descripcion}</Text>
          {/* Agrega más detalles según tus necesidades */}
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'center',
    padding: 10,
  },
  backButton: {
    marginRight: 10,
  },
  contentContainer: {
    flexDirection: 'row',
  },
  imageContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  image: {
    width: '100%',
    height: '100%',
    borderRadius: 8,
  },
  detailsContainer: {
    flex: 1,
    padding: 20,
  },
  productName: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
    marginBottom: 10,
  },
  detailsText: {
    fontSize: 18,
    marginBottom: 8,
  },
});

export default ProductDetailScreen;
