import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TextInput, Image, Pressable, ScrollView } from 'react-native';
import { Formik } from 'formik';

export default function App() {
  const handleButtonPress = () => {
    alert('Botón presionado');
  };
  const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

  // Define el tamaño máximo deseado para la imagen
  const maxImageSize = Math.min(screenWidth, screenHeight) * 0.4;
  return (
    <ScrollView contentContainerStyle={styles.allContainer}>
      <View style={styles.formContainer}>
        <Text style={styles.header}>Inicio de Sesión</Text>
        <View style={styles.logoContainer}>
          <Image
            source={require('./assets/img/GIcon.png')}
            style={styles.image}
          />
        </View>
        <Formik
          initialValues={{ nombre: '', correo: '' }}
          onSubmit={(values) => {
            // Aquí puedes manejar los valores del formulario cuando se envía.
            console.log(values);
          }}
        >
          {({ handleChange, handleBlur, handleSubmit, values }) => (
            <View>
              <TextInput
                placeholder="Nombre"
                onChangeText={handleChange('nombre')}
                onBlur={handleBlur('nombre')}
                value={values.nombre}
                style={styles.input}
              />
              <TextInput
                placeholder="Correo"
                onChangeText={handleChange('correo')}
                onBlur={handleBlur('correo')}
                value={values.correo}
                style={styles.input}
              />
              <Pressable title="Enviar" onPress={handleSubmit} />
            </View>
          )}
        </Formik>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  allContainer: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
  },
  header: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
    marginBottom: 10,
    marginTop: 10,
  },
  formContainer: {
    alignItems: 'center',
    width: '90%', // Utilizamos un porcentaje para el ancho
    padding: 20,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5,
  },
  image: {
    width: '100%', // Utilizamos un porcentaje para el ancho
    aspectRatio: 1, // Mantenemos la relación de aspecto
  },
  logoContainer: {
    width: '100%',
    aspectRatio: 1, // Mantenemos la relación de aspecto del contenedor
  },
});
